
$(document).ready(function(){var b=12;var d=0;var c="inactive";function a(e,f){$.ajax({url:CIURLS.siteurl+"index/load_more_data",method:"POST",data:{csrf_test_name:$.cookie("csrf_cookie_name"),limit:e,start:f},cache:false,success:function(g){$("#load_data").append(g);if(g===""){$("#load_data_message").html("<button type='button' class='btn btn-info'>No Data Found</button>");c="active"}else{$("#load_data_message").html("<button type='button' class='btn btn-info'>Please wait...</button>");c="inactive"}}})}if(c==="inactive"){c="active";a(b,d)}$(window).scroll(function(){if($(window).scrollTop()+$(window).height()>$("#load_data").height()&&c==="inactive"){c="active";d=d+b;setTimeout(function(){a(b,d)},1000)}});$(window).scroll(function(){if($(this).scrollTop()>50){$("#back-to-top").fadeIn()}else{$("#back-to-top").fadeOut()}});$("#back-to-top").click(function(){$("#back-to-top").tooltip("hide");$("body,html").animate({scrollTop:0},800);return false});$("#back-to-top").tooltip("show")});